mapper.select:
SELECT count(*)
FROM rvw_lk_adm
WHERE usr_id = #{usr_id}
  AND rvw_no = #{rvw_no}

SELECT rvw_lk_yn
FROM rvw_lk_adm
WHERE usr_id = #{usr_id}
  AND rvw_no = #{rvw_no}

mapper.update:
UPDATE rvw_lk_adm
SET rvw_lk_yn = 1,
    fnl_mod_no = #{usr_id},
    fnl_mod_date = CURRENT_TIMESTAMP
WHERE usr_id = #{usr_id}
  AND rvw_no = #{rvw_no}

UPDATE rvw_lk_adm
SET rvw_lk_yn = 0,
    fnl_mod_no = #{usr_id},
    fnl_mod_date = CURRENT_TIMESTAMP
WHERE usr_id = #{usr_id}
  AND rvw_no = #{rvw_no}
