mapper.update:
UPDATE rvw
SET rvw_vcnt = rvw_vcnt + 1
WHERE rvw_no = #{rvw_no}

UPDATE rvw
SET cmt_cnt = cmt_cnt + #{cnt}
WHERE rvw_no = #{rvw_no}

mapper.select:
SELECT prd_cd
FROM trv_prd_dtl
WHERE prd_dtl_cd = #{prd_dtl_cd}

SELECT count(*)
FROM rvw
WHERE usr_id = #{usr_id}
AND rvw_no = #{rvw_no}

mapper.update:
UPDATE rvw
SET lk_cnt = lk_cnt + 1
WHERE rvw_no = #{rvw_no}

UPDATE rvw
SET lk_cnt = lk_cnt - 1
WHERE rvw_no = #{rvw_no}

mapper.select:
SELECT A.*
FROM rvw A INNER JOIN trv_prd B ON A.prd_cd = B.prd_cd
WHERE B.cntn_cd = #{cntn_cd};
